mapper.select:
select id, `code`, parentCode, `name`, url, sort, type, menuShow, createTime, updateTime, availableStatus
from Resource
where id = #{id}

select r.id, r.`code`, r.parentCode, r.`name`, r.url, r.sort, r.type, r.menuShow, r.createTime, r.updateTime, r.availableStatus
from Resource r

    right join (select distinct rr.resourceCode
    from RoleResource rr
    right join AdminRole ar on ar.roleCode = rr.roleCode
    where ar.adminId = #{selfId}
    ) t on t.resourceCode = r.code




            r.availableStatus in

                #{item}

            and

select resourceCode
from RoleResource
where roleCode = #{roleCode}
